sources.src/scss/_base.scss:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  overflow: hidden;
}

input:focus,
textarea:focus,
button {
  outline: none;
}
button {
  background: none;
  border: none;
  cursor: pointer;
}

html {
  font-size: 62.5%;
  scroll-behavior: smooth;
  font-family: Tahoma;
  overflow-x: hidden;
  width: 100%;
}

.wrapper {
  width: 100%;
  max-width: 120rem;
  margin: 0 auto;
  padding: 0 1.5rem;
}

a {
  text-decoration: none;
  color: black;
}

ul {
  list-style: none;
}

img {
  max-width: 100%;
}

sources.src/scss/_header.scss:
.header {
  position: fixed;
  top: 0;
  right: 0;
  left: 0;
  bottom: 0;
  height: 9rem;
  z-index: 5;
  display: flex;
  align-items: center;
  background-color: transparent;

  &__basket-icon {
    transition: transform 0.2s;

    &:hover {
      display: inline-block;
      transform: scale(1.2);
    }
  }

  .wrapper {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  &__logo {
    @include large {
      display: none;
    }
    &--mobile {
      display: none;
      text-transform: uppercase;
      letter-spacing: 1rem;
      transition: transform 0.5s linear;
      @include large {
        display: block;
      }
    }
  }
  &.is-scroll {
    background-color: white;
  }
}

sources.src/scss/_nav.scss:
.header__nav {
  display: flex;
  &-container {
    display: flex;
    align-items: flex-end;

    @include large {
      opacity: 0;
      visibility: hidden;
      display: flex;
      flex-direction: column;
      justify-content: space-around;
      align-items: center;
      padding: 8rem 2rem 4rem;
      text-align: center;
      position: fixed;
      top: 0;
      bottom: 0;
      left: 0;
      right: 0;
      background-color: white;
      font-size: 2rem;
      .is-nav-open & {
        opacity: 1;
        visibility: visible;
      }
    }
  }

  &-item {
    margin-right: 5rem;

    @include large {
      margin-right: 0;
      margin-bottom: 1em;
    }
  }

  &-link {
    text-transform: uppercase;
    font-size: 1.6rem;
    opacity: 0.5;
    transition: opacity 0.2s;

    @include large {
      font-size: 3rem;
    }

    &:hover {
      opacity: 1;
    }

    &--shop {
      display: inline-block;
      opacity: 1;
      transition: border-bottom 0.2s;

      &:hover {
        border-bottom: 2px solid black;
      }
    }
  }
}

.nav__icon {
  display: none;
  width: 40px;
  height: 35px;
  position: relative;
  transform: rotate(0deg);
  transition: 0.5s ease-in-out;
  cursor: pointer;
  z-index: 9999;

  @include large {
    display: block;
  }
}

.nav__item {
  display: block;
  position: absolute;
  height: 5px;
  width: 100%;
  border-radius: 1rem;
  opacity: 1;
  right: 0;
  transform: rotate(0deg);
  transition: 0.25s ease-in-out;
  background: black;

  &:nth-child(1) {
    top: 0px;

    .is-nav-open & {
      top: 18px;
      transform: rotate(135deg);
    }
  }

  &:nth-child(2) {
    top: 10px;

    .is-nav-open & {
      opacity: 0;
      left: -60px;
    }
  }

  &:nth-child(3) {
    top: 20px;

    .is-nav-open & {
      top: 18px;
      transform: rotate(-135deg);
    }
  }
}

sources.src/scss/_slider.scss:
:root {
  --swiper-navigation-size: 3.5rem;
  --swiper-theme-color: black;
}
.slider {
  background-color: #f5f5f5;
  min-height: 100vh;
}

.swiper-pagination-custom {
  bottom: 10%;
  left: 35%;
  font-size: 1.6rem;
}

.swiper {
  &-container {
    max-width: 117rem;
    width: 100%;
    max-height: 100vh;
    height: 100vh;
    overflow: initial;
    &:not(.swiper-container-initialized) .swiper-slide:not(:first-child) {
      display: none;
    }
  }

  &-slide {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: 1fr;
    opacity: 0;
    visibility: hidden;
    transition: visibility 0.9s, opacity 0.9s;
    @include medium {
      grid-template-columns: 1fr;
      grid-template-rows: repeat(2, 1fr);
    }
    &-active,
    &-duplicate-active {
      opacity: 1;
      visibility: visible;
    }
  }
  &-description {
    grid-area: 1 / 3 / 2 / 4;
    align-self: center;
    font-family: 'Nothing You Could Do';
    font-size: 3rem;
    @include medium {
      font-size: 2rem;
      grid-area: 2 / 1 / 3 / 2;
      justify-self: center;
      margin-bottom: 2rem;
    }
  }

  &-photo {
    grid-area: 1 / 1 / 2 / 3;
    justify-self: right;
    @include medium {
      grid-area: 1 / 1 / 2 / 2;
    }
    &-plant3 {
      width: 50%;
      justify-self: center;
      align-self: center;
    }
  }
}

sources.src/scss/_arrow.scss:
.arrow__down {
  position: absolute;
  bottom: 5%;
  left: 50%;
  transform: translate(-50%, -5%);
  color: black;
  font-size: 3rem;
  opacity: 0.6;
  overflow: hidden;
  text-shadow: 0 -1.5rem black, 0 -3rem black;
  cursor: pointer;
  z-index: 5;
  font-weight: 200;

  &:hover {
    opacity: 1;
  }

  .fa {
    animation: arrow 2s linear infinite;
  }
}

@keyframes arrow {
  from {
    transform: translateY(-200%);
  }

  to {
    transform: translateY(200%);
  }
}

